.switch-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 15px;
  background-color: #f7f7f7;
  border-radius: 10px;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.1);
  max-width: 500px;
  margin: 0 auto;
}

/* Defini√ß√£o da parte esquerda (texto e bot√£o "i") */
.switch-info {
  display: flex;
  align-items: center;
  column-gap: 10px;
}

.switch-info span {
  font-size: 14px;
  color: #333;
}

.info-button {
  border: none;
  background-color: transparent;
  color: #888;
  font-size: 14px;
  font-weight: bold;
  cursor: pointer;
}

.info-button:hover {
  color: #555;
}

/* Toggle button */
.toggle-switch {
  position: relative;
  display: inline-block;
  width: 40px;
  height: 20px;
}

.toggle-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.slider {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  transition: 0.4s;
  border-radius: 20px;
}

.slider:before {
  position: absolute;
  content: "";
  height: 16px;
  width: 16px;
  left: 2px;
  bottom: 2px;
  background-color: white;
  transition: 0.4s;
  border-radius: 50%;
}

/* Quando o input est√° marcado */
input:checked + .slider {
  background-color: #24ca68;
}

input:checked + .slider:before {
  transform: translateX(20px);
}


