    <script>
    // Se houver JS custom do usu√°rio
    
    // Abre/fecha modal principal
    function openDonateModal() {
        document.getElementById('donateModal').style.display = 'flex';
    }
    function closeDonateModal() {
        document.getElementById('donateModal').style.display = 'none';
    }

    // Modal "Outro valor"
    function openCustomValueModal(){
        closeDonateModal(); // fecha o modal principal
        document.getElementById('customValueModal').style.display = 'flex';
    }
    function closeCustomValueModal(){
        document.getElementById('customValueModal').style.display = 'none';
    }

    // Formatador real-time para R$ (similar ao ajude-vakinha)
    function toBRL(v){
        v = v.replace(/\D/g,'');
        if (!v) return '';
        v = (v / 100).toFixed(2).replace('.', ',');
        return v.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
    }

    const moneyInp = document.getElementById('moneyInput');
    if(moneyInp) {
        moneyInp.addEventListener('input', e => {
            let curPos = e.target.selectionEnd;
            e.target.value = 'R$ ' + toBRL(e.target.value);
            e.target.selectionEnd = curPos + 1;
        });
    }

    // Sugest√µes de valor
    document.querySelectorAll('.btn-sug').forEach(button => {
        button.addEventListener('click', () => {
            let value = button.getAttribute('data-v');
            moneyInp.value = 'R$ ' + toBRL(value + '00');
        });
    });

    // Submete valor custom
    function submitValue(){
        let raw = moneyInp.value.replace(/\D/g,'');
        if (!raw) {
            alert('Digite um valor');
            return;
        }
        let amount = parseInt(raw, 10) / 100; 
        amount = Math.floor(amount);
        let url = "" + (true ? '?' : '&') + "amount=" + amount;
                window.open(url, '_blank');
    }
    </script>
    
