const toastContainer=document.getElementById("toastContainer");function showToast(msg){const div=document.createElement("div");div.className="toast";div.textContent=msg;toastContainer.prepend(div);setTimeout(()=>div.classList.add("show"),100);setTimeout(()=>{div.classList.remove("show");setTimeout(()=>div.remove(),300);},3000);}
