// formata em BRL
function formatarMoeda(valor) {
  return new Intl.NumberFormat("pt-BR", { style: "currency", currency: "BRL" }).format(valor);
}

// Fun√ß√£o para atualizar os valores e barras de progresso
function atualizarProgresso(valor) {
  // Pegando a meta (em PHP) no formato '80.000,00'
  const metaPhp = "<?php echo $meta; ?>"; 
  const meta = parseFloat(metaPhp.replace(/\./g, '').replace(',', '.'));  // Convertendo para n√∫mero

  // Pegando o valor atual doado a partir de #valorMobile (no formato '9.752,00')
  const valorAtual = parseFloat(document.getElementById('valorMobile').innerText.replace('.', '').replace(',', '.')) || 0;

  // Calculando o novo valor total somando o valor da doa√ß√£o
  const novoValor = valorAtual + valor;

  // Calculando a porcentagem
  const porcentagem = (novoValor / meta) * 100;

  // Registro no console: apenas os valores necess√°rios
    /*
  console.group("Altera√ß√µes:");
  console.log("#porcentagem (atual):", ((valorAtual / meta) * 100).toFixed(2) + "%");  // Porcentagem antes da altera√ß√£o
  console.log("#porcentagem (novo):", porcentagem.toFixed(2) + "%");  // Nova porcentagem
  console.log("#valorMobile (novo):", novoValor);  // Novo valor de #valorMobile
  console.groupEnd();
    */

  // Atualizando os valores na interface
  document.getElementById('valorMobile').innerText = novoValor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' }).replace('R$', '').trim();
  document.getElementById('doado').innerText = novoValor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });

  // Atualizando as barras de progresso
  document.getElementById('barra').style.width = porcentagem.toFixed(2) + '%';
  document.getElementById('barraMobile').style.width = porcentagem.toFixed(2) + '%';

  // Atualizando a porcentagem
  document.getElementById('porcentagem').innerText = porcentagem.toFixed(2) + '%';
}


// cria e mostra o bal√£o
function exibirNotificacao(nome, image, valor) {
  const notificacao = document.createElement("div");
  notificacao.className = "notificacao";
  notificacao.innerHTML = `
    <div class="avatar"><img src="${image}" alt="${nome}"></div>
    <div class="content"><h4>${nome}</h4> Acabou de doar <strong>${formatarMoeda(valor)}</strong>.</div>
  `;
  document.body.appendChild(notificacao);
    atualizarProgresso(valor);

  // confete
  setTimeout(() => {
    const rect = notificacao.getBoundingClientRect();
    confetti({
      particleCount: 100,
      spread: 70,
      origin: {
        x: (rect.left + rect.width / 2) / window.innerWidth,
        y: (rect.top + rect.height / 2) / window.innerHeight
      }
    });
  }, 100);

  // esmaece e remove ap√≥s 6s
  setTimeout(() => {
    notificacao.style.transform = "translateY(0)";
    notificacao.style.opacity = "0";
    setTimeout(() => notificacao.remove(), 500);
  }, 6000);
}

// ciclo dos bal√µes
function atualizarBalonzinhos() {
  const item = arrayDados[index];
  exibirNotificacao(item.nome, item.image, item.doado);
  index = (index + 1) % arrayDados.length;
}

// mostra a cada 30s
setInterval(atualizarBalonzinhos, 30000);

// espera 10s ap√≥s carregar pra mostrar o primeiro
document.addEventListener("DOMContentLoaded", () => {
  setTimeout(atualizarBalonzinhos, 4000);
});
